import React from 'react';
// import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import './ExpenseItem.css';

const ExpenseItem = ({ timepoint, title, amount }) => {

  const year = timepoint.toLocaleString("en", { year: "numeric" });
  const month = timepoint.toLocaleString("en", { month: "long" });
  const day = timepoint.toLocaleString("en", { day: "numeric" });

  return (
    <Card className="expense-item">
      <div className="expense-item__date">
      <span className="expense-item__date--year">{year}</span>
      <span className="expense-item__date--month">{month}</span>
      <span className="expense-item__date--day">{day}</span>
      </div>
      <h2 className="expense-item__title">{title}</h2>
      <div className="expense-item__amount">${amount}</div>
    </Card>
  );
}

export default ExpenseItem;
