import { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState('');
  const [enteredDate, setEnteredDate] = useState('');

  const handleTitleChange = e => { setEnteredTitle(e.target.value); }

  const handleAmountChange = (e) => {
    setEnteredAmount(e.target.value);
  }

  const handleDateChange = (e) => {
    setEnteredDate(e.target.value);
  }

  return (
    <form className="new-expense__form">
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label htmlFor="title">Title</label>
          <input id="title" type="text" onChange={handleTitleChange} />
        </div>
        <div className="new-expense__control">
          <label htmlFor="amount">Amount</label>
          <input id="amount" type="number" min="0.01" step="0.01" onChange={handleAmountChange}/>
        </div>
        <div className="new-expense__control">
          <label htmlFor="date">Date</label>
          <input id="date" type="date" min="2019-01-01" max="2022-12-31" onChange={handleDateChange}/>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
      <p>{enteredTitle}</p>
      <p>{enteredAmount}</p>
      <p>{enteredDate}</p>
    </form>
  );
}

export default ExpenseForm;
