import React from "react";
import "./ExpenseDate.css";

const ExpenseDate = ({ expensedate }) => {
  const year = expensedate.toLocaleString("en", { year: "numeric" });
  const month = expensedate.toLocaleString("en", { month: "long" });
  const day = expensedate.toLocaleString("en", { day: "numeric" });

  return (
    <>
      <span className="expense-item__date--year">{year}</span>
      <span className="expense-item__date--month">{month}</span>
      <span className="expense-item__date--day">{day}</span>
    </>
  );
};

export default ExpenseDate;
