import React from 'react';
import './ExpensesFilter.css';

const ExpensesFilter = () => {

  const handleEnteredYear = (e) => {
    e.preventDefault();
    console.log(e.target.value);
  }

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label htmlFor="year">Filter by year</label>
        <select id="year">
          <option value="2022" onChange={handleEnteredYear}>2022</option>
          <option value="2021" onChange={handleEnteredYear}>2021</option>
          <option value="2020" onChange={handleEnteredYear}>2020</option>
          <option value="2019" onChange={handleEnteredYear}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
